package com.example.list.presentation.main

import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.list.R
import com.example.list.domain.Song
import com.example.list.presentation.adapters.song.SongAdapter
import java.util.ArrayList

class MainActivity : AppCompatActivity() {

    lateinit var rvSongs : RecyclerView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        rvSongs = findViewById(R.id.rvSongs)

        val songs = ArrayList<Song>()
        songs.add((Song("19-2000",4100,"Gorillaz","https://static.wikia.nocookie.net/gorillaz/images/8/84/Gorillaz_Phase_One_-_Celebrity_Takedown.jpg/revision/latest?cb=20100810060005&path-prefix=es")))
        songs.add((Song("America",3500,"Rammstein","https://rockandblog.net/wp-content/uploads/2020/09/rammstein-new-album-2021.jpg")))
        songs.add((Song("Chocolate",2600,"Limp Bizkit","data:image/jpeg;base64,")))
        songs.add((Song("Waterfall",5500,"Megadeth","https://i0.wp.com/www.scienceofnoise.net/wp-content/uploads/2020/09/rust-in-peace-5cd5b40e937e4.jpg")))
        songs.add((Song("Cementery Gates",4500,"Pantera","https://static.wikia.nocookie.net/metal/images/9/9f/Panterafoto.jpg/revision/latest?cb=20081201214914&path-prefix=es")))

        val adapter = SongAdapter(this, songs)
        rvSongs.setHasFixedSize(true)
        rvSongs.layoutManager = LinearLayoutManager(this)
        rvSongs.adapter = adapter

    }
}